/*Animation*/
@media (min-width: 500px) {

   @-webkit-keyframes hide {
      0% { -webkit-transform: translateX(0px); opacity: 1; z-index: -50; }
      100% { -webkit-transform: translateX(-30px); opacity: 0; z-index: -50; }
   }
   @-moz-keyframes hide {
      0% { -moz-transform: translateX(0px); opacity: 1; z-index: -50; }
      100% { -moz-transform: translateX(-30px); opacity: 0; z-index: -50; }
   }

   @-webkit-keyframes reveal {
      0% { -webkit-transform: translateX(-30px); opacity: 1; z-index: -50; }
      100% { -webkit-transform: translateX(0); opacity: 1; z-index: -50; }
   }
   @-moz-keyframes reveal {
      0% { -moz-transform: translateX(-30px); opacity: 1; z-index: -50; }
      100% { -moz-transform: translateX(0); opacity: 1; z-index: -50; }
   }

   @-webkit-keyframes slide-up {
      0% { -webkit-transform: translateY(5px); opacity: 0; }
      100% { -webkit-transform: translateY(0); opacity: 1; }
   }
   @-moz-keyframes slide-up {
      0% { -moz-transform: translateY(5px); opacity: 0; }
      100% { -moz-transform: translateY(0); opacity: 1; }
   }

   @-webkit-keyframes slide-down {
      0% { -webkit-transform: translateY(0); opacity: 1; visibility: visible; }
      100% { -webkit-transform: translateY(5px); opacity: 0; visibility: visible; }
   }
   @-moz-keyframes slide-down {
      0% { -moz-transform: translateY(0); opacity: 1; visibility: visible; }
      100% { -moz-transform: translateY(5px); opacity: 0; visibility: visible; }
   }

   @-webkit-keyframes pagination-newer {
      0% { -webkit-transform: translateX(-42px); opacity: 0.2; }
      100% { -webkit-transform: translateX(0); opacity: 1; }
   }
   @-moz-keyframes pagination-newer {
      0% { -moz-transform: translateX(-42px); opacity: 0.2; }
