   }
    @-webkit-keyframes pagination-newer-off {
      0% { -webkit-transform: translateX(0); opacity: 1; }
      50% { -webkit-transform: translateX(0); opacity: 1; }
      100% { -webkit-transform: translateX(-42px); opacity: 0.2; }
   }
