/*Styles*/
body { 
    background: #ebecec;
}

body {
    color: #374248;
    font-family: "Lucida Grande", Arial, Helvetica, sans-serif;
    font-size: 13px;
    font-weight: 500;
    margin: 0;
}

h1, h2, blockquote, time {
    font-family: "museo-sans-1","museo-sans-2",sans-serif;
    margin: 4px 0;
    -webkit-font-smoothing: antialiased;
}
.promo {
    font-family: "museo-sans-1","museo-sans-2",sans-serif;
    -webkit-font-smoothing: subpixel-antialiased;
}
.post-content blockquote p {
    border-left: 3px solid #1e91d0;
    font-size: 20px;
    line-height: 1.6;
    padding: 0 0 0 30px;
}
a { color: #0093da; text-decoration: none; }
a:hover { color: #006da2; }
p, span { margin: 0; -webkit-font-smoothing: subpixel-antialiased; }

#tumblr_controls { 
    top: 17px !important;
    right: 50% !important;
    margin-right: -407px;
}
.tumblr_sticky_iframe {
    padding-top: 17px;
}

.wrapper {
    margin: 0 auto;
    max-width: 960px;
    padding: 10px 0;
}

#masthead {
    background: #384248;
    background: rgba(52, 65, 72, 0.95);
    height: 60px;
    position: fixed;
    width: 100%;
    z-index: 10;
}

    .main-nav {
        display: inline-block;
        margin: 0 10px 0 0;
    }
    .main-nav ul {
        font-size: 11px;
        font-weight: 700;
        list-style: none;
        margin: 13px 0;
        padding: 0 0 0 30px;
    }
    .main-nav ul li {
        display: inline-block;
    }
    .main-nav ul li a {    
        color: #9fa5a8;
        display: inline-block;
        text-decoration: none;
    }
    .main-nav ul li a:hover {
        color: #fff;
    }
    .social {
        float: right;
        display: inline-block;
        padding: 10px 30px 0 0;
        margin: 0;
    }
    .social li {
        -moz-border-radius: 2px;
        border-radius: 2px;
        background: #3e3e3e;
        background: rgba(0,0,0,0.38);
        border: 1px solid rgba(0,0,0,0.18);
        text-shadow: 1px 1px 0 rgba(0,0,0,0.08);
