        margin: 0 0 0 3px;
    }
    .social li a {
        display: block;
    }
    .social li.twitter a {
        background: url(http://virb.com/_assets/_blog/twitter-2x.png) center no-repeat;
        background-size: 10px;
        height: 18px;
        width: 18px;
        text-indent: -9999px;
    }
    .social li.facebook a {
        background: url(http://virb.com/_assets/_blog/facebook-2x.png) center no-repeat;
        background-size: 10px;
        height: 18px;
        width: 18px;
        text-indent: -9999px;
    }

#site_name { 
    background: #fff;
    padding-top: 60px;
}
    #site_name .wrapper {
        max-width: 580px;
        padding: 40px 30px;
    }
    #site_name h1 {
        display: block;
        float: left;
        margin: 3px 30px 0 0;
    }
    #site_name a {
        background: url(http://g.virbcdn.com/_assets/_graphics/virb_navlogo.png) top left no-repeat;
        background-size: 40px 40px;
        display: block;
        height: 40px;
        text-indent: -9999px;
        width: 40px;
    }
    #site_name p {
        display: block;
        float: left;
        font-size: 13px;
        line-height: 1.6;
    }
    #site_name p b {
        font-size: 15px;
    }

#content {
    margin: 0;
}
article:first-child .post-title {
    margin-top: 0;
}

    .panorama .photo-wrapper:after,
    .photo .photo-wrapper:after,
    .post-title:after,
    .metadata .author .name-wrap #name:after {
        content: "";
        display: block;
        height: 14px;
        width: 14px;
        background: #fff;
        position: absolute;
        bottom: 0px;
        margin: 0 0 -10px -7px;
        left: 50%;
        -webkit-transform: rotate(45deg);
        -moz-transform: rotate(45deg);
        -ms-transform: rotate(45deg);
        -o-transform: rotate(45deg);
        filter: progid:DXImageTransform.Microsoft.BasicImage(rotation=3);
    }

    .post-title {
        display: block;
        text-align: center;
        max-width: 580px;
        margin: 20px auto 0 auto;
        padding: 40px 4% 30px 4%;
        position: relative;
    }
    .post-title h1 a {
        color: #374248;
        font-size: 34px;
        font-weight: 200;
        text-decoration: none;
    }
    .post-title h1 a:hover {
        opacity: 0.8;
    }
    .post-title time {
        color: #1e91d0;
        display: inline-block;
        font-size: 10px;
        font-weight: 700;
        text-transform: uppercase;
    }
    .post-title p {
        margin: 5px 0;
    }

    .post-content {
        -moz-border-radius: 4px;
        border-radius: 4px;
        background: #fff;
        margin: 0 auto;
        max-width: 500px;
        padding: 40px 40px 18px 40px;
    }
    .post-content p {
        font-size: 13px;
        line-height: 1.8;
        margin: 0 0 22px 0;
    }
    .post-content img {
        border: 1px solid #d6d7d8;
        max-width: 100%;
    }
    .post-content a:visited img {
        border: 1px solid #d6d7d8;
    }
    .post-content ol li,
    .post-content ul li {
        font-size: 13px;
        line-height: 1.8;
    }

.metadata {
    max-width: 580px;
    margin: 0 auto;
    padding: 20px 0 20px 0;
}
    .metadata .wrapper {
        padding: 0 0 0 50px;
        height: 30px;
        overflow: visible;
        width: 180px;
    }
        
        .metadata .author {
            display: inline-block;
            float: left;
            position: relative;
            overflow: visible;
        }
        .metadata .author img {
            margin: 0 20px 0 0;
            -moz-border-radius: 3px;
            border-radius: 3px;
            max-width: 30px;
        }
        .metadata .author .name-wrap {
            bottom: 39px;
            height: 28px;
            margin-left: -110px;
            position: absolute;
            text-align: center;
            width: 250px;
            overflow: visible;
            visibility: hidden;
            z-index: 1;

            -webkit-animation: slide-down 0.3s ease 0s 1 normal;
            -moz-animation: slide-down 0.3s ease 0s 1 normal;
        }
        .metadata .author:hover .name-wrap {
            visibility: visible;

            -webkit-animation: slide-up 0.3s ease 0s 1 normal;
            -moz-animation: slide-up 0.3s ease 0s 1 normal;
        }
        .metadata .author #name {
            background: #fff;
            border: 1px solid #ebecec;
            -moz-border-radius: 3px;
            border-radius: 3px;
            display: inline-block;
            padding: 6px 8px;
            white-space: nowrap;
        }
        .metadata .author .name-wrap #name:after {
            height: 6px;
            width: 6px;
            margin: 0 0 -4px -3px;
        }

        .metadata .like {
            display: inline-block;
            float: left;
            overflow: hidden;
            width: auto;
        }
        .metadata .like .like_button {
            -moz-border-radius: 3px;
            border-radius: 3px;
            background: #fff;
            float: left;
            width: 30px !important;
        }
        .metadata .like .like_button:hover {
            background: #f9f9f9;
        }
        .metadata .like .like_button iframe {
            -moz-border-radius: 3px;
            border-radius: 3px;
            padding: 4px 8px 0;
            width: 30px;
            height: 27px;
            -webkit-box-sizing: border-box;
            -moz-box-sizing: border-box;
            box-sizing: border-box;
        }
        .metadata .like .index {
            -moz-border-top-right-radius: 3px;
            border-top-right-radius: 3px;
            -moz-border-bottom-right-radius: 3px;
            border-bottom-right-radius: 3px;
            background: #dee0e1;
            float: left;
            height: 30px;
            opacity: 0;
            padding: 7px 9px 0 9px;
            text-align: center;
            width: auto;
            -webkit-box-sizing: border-box;
            -moz-box-sizing: border-box;
            box-sizing: border-box;

            -webkit-animation: hide 0.3s ease 0s 1 normal;
            -moz-animation: hide 0.3s ease 0s 1 normal;
        }
        .metadata .like .index a {
            color: #374248;
            text-decoration: none;
        }
        .metadata .like .index a:hover {
            color: #374248;
        }
        .metadata .wrapper .like.hover .index { 
            display: block; 
            opacity: 1;
            z-index: 0;

            -webkit-animation: reveal 0.3s ease 0s 1 normal;
            -moz-animation: reveal 0.3s ease 0s 1 normal;
        }
        .metadata .reblog_button {
            -moz-border-radius: 3px;
            border-radius: 3px;
            background: #fff;
            display: inline-block;
            float: left;
            height: 30px !important;
            margin: 0 20px 0 0;
            padding: 7px;
            width: 30px !important;
            -webkit-box-sizing: border-box;
            -moz-box-sizing: border-box;
            box-sizing: border-box;
        }
        .metadata .reblog_button:hover {
            background: #f9f9f9;
        }
        .metadata .photo-pano-time {
            display: inline-block;
            float: left;
            margin: 3px 20px 0 0;
            border-right: 2px solid #dee0e1;
        }
        .metadata .photo-pano-time time {
            color: #1e91d0;
            display: inline-block;
            font-size: 10px;
            font-weight: 700;
            padding: 6px 20px 6px 0;
            text-transform: uppercase;
        }
        article:last-child .metadata {
            margin-bottom: 75px;
        }

    /*--- Photo + Panorama Post---*/

    .panorama,
    .photo {
        background: #fff;
        line-height: 0;
        text-align: center;
    }

        .panorama .photo-wrapper,
        .photo .photo-wrapper {
            display:-moz-inline-stack;
            display: inline-block;
            position: relative;
            width: auto;
            margin: 0 auto;
        }
        .panorama .photo-wrapper a,
        .panorama .photo-wrapper img,
        .photo .photo-wrapper a,
        .photo .photo-wrapper img {
            background: #fff;
            display: inline-block;
            line-height: 0;
            max-width: 100%;
            padding: 0;
        }
        .panorama .photo-wrapper img,
        .photo .photo-wrapper img {
            display: block;
            width: 100%;
        }
        .panorama .caption,
        .photo .caption {
            background: #fff;
            bottom: 0px;
            line-height: 1.6;
            position: absolute;
            padding: 25px 28px;
            visibility: hidden;
            width: 100%;
            margin: 0 auto;
            -webkit-box-sizing: border-box;
            -moz-box-sizing: border-box;
            box-sizing: border-box;

            -webkit-animation: slide-down 0.3s ease 0s 1 normal;
            -moz-animation: slide-down 0.3s ease 0s 1 normal;
        }
        .panorama .caption.hover,
        .photo .caption.hover {
            visibility: visible;

            -webkit-animation: slide-up 0.3s ease 0s 1 normal;
            -moz-animation: slide-up 0.3s ease 0s 1 normal;
        }
        .panorama .caption:hover,
        .photo .caption:hover {
            visibility: visible;
            opacity: 1;
            -webkit-animation: none;
            -moz-animation: none;
        }

        .metadata.photo-pano .wrapper {
            width: 270px;
        }

#pagination {

}
    .left-pagination,
    .right-pagination {
        display: inline-block;
        margin-top: -15px;
        opacity: 0.2;
        overflow: hidden;
        position: fixed;
        top: 50%;
    }
    .left-pagination {
        -webkit-transform: translateX(-42px);
        -moz-transform: translateX(-42px);
        -moz-border-top-right-radius: 2px;
        -moz-border-bottom-right-radius: 2px;
        border-top-right-radius: 2px;
        border-bottom-right-radius: 2px;
        left: 0;

        -webkit-animation: pagination-newer-off 0.6s ease 0s 1 normal;
        -moz-animation: pagination-newer-off 0.6s ease 0s 1 normal;
    }
    .right-pagination {
        -webkit-transform: translateX(38px);
        -moz-transform: translateX(38px);
        -moz-border-top-left-radius: 2px;
        -moz-border-bottom-left-radius: 2px;
        border-top-left-radius: 2px;
        border-bottom-left-radius: 2px;
        right: 0;

        -webkit-animation: pagination-older-off 0.6s ease 0s 1 normal;
        -moz-animation: pagination-older-off 0.6s ease 0s 1 normal;
    }
    .left-pagination:hover {
        opacity: 1;
        -webkit-transform: translateX(0);
        -moz-transform: translateX(0);

        -webkit-animation: pagination-newer 0.3s ease 0s 1 normal;
        -moz-animation: pagination-newer 0.3s ease 0s 1 normal;
    }
    .right-pagination:hover {
        opacity: 1;
        -webkit-transform: translateX(0);
        -moz-transform: translateX(0);
        -webkit-animation: pagination-older 0.3s ease 0s 1 normal;
        -moz-animation: pagination-older 0.3s ease 0s 1 normal;
    }
    .left-pagination a,
    .right-pagination a {
        color: #fff;
        background: #384248;
        background: rgba(52, 65, 72, 0.95);
        display: inline-block;
        font-size: 10px;
        padding: 10px;
        width: auto;
        -webkit-box-sizing: border-box;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
    }
    .left-pagination a span,
    .right-pagination a span {
        display: inline-block;
        width: 20px;
    }
    .left-pagination a span {
        text-align: right;
    }

